$experimentToExport.function += @(
    '_Compare-GitConfig_dev'
)
$experimentToExport.alias += @(

    'git->ExampleConfig🔖'
)

function _Compare-GitConfig_dev {
    <#
    .synopsis
        old code. Compare git configs [Dev.Nin Example]
    #>
    [Alias('git->ExampleConfig🔖')]
    [cmdletbinding()]
    param()

    '# Generated by: --global --list'
    | Set-Content temp:\conf1

    "`n`n" -f @(Get-Item . )
    | Add-Content temp:\conf1

    git config --global --list
    | Add-Content temp:\conf1

    '# Generated by: --local --list'
    | Set-Content temp:\conf2
    "# path: '{0}'`n`n" -f @(Get-Item . )
    | Add-Content temp:\conf2

    git config --local --list
    | Add-Content temp:\conf2
    codei -r --diff (Get-Item -ea stop temp:\conf1) (Get-Item -ea stop temp:\conf2)
}
