$experimentToExport.function += @(
    'Compare-GitConfig-Dev🐒'
)
$experimentToExport.alias += @(
    'Example🔖Compare-GitConfig-Dev🐒' # this alias is really more for tagging,
)

function Compare-GitConfig-Dev🐒 {
    <#
    .synopsis
        Compare git configs [Dev.Nin Example]
    #>
    [Alias('Example🔖Compare-GitConfig-Dev🐒')]
    [cmdletbinding()]
    param()

    '# Generated by: --global --list' | Set-Content temp:\conf1
    "`n`n" -f @(Get-Item . ) | Add-Content temp:\conf1
    git config --global --list | Add-Content temp:\conf1

    '# Generated by: --local --list' | Set-Content temp:\conf2
    "# path: '{0}'`n`n" -f @(Get-Item . ) | Add-Content temp:\conf2
    git config --local --list | Add-Content temp:\conf2
    codei -r --diff (Get-Item -ea stop temp:\conf1) (Get-Item -ea stop temp:\conf2)
}
